import React, { useReducer } from "react";
import {
  Segment,
  Grid,
  Form,
  Button,
  Divider,
  Dimmer,
  Loader,
} from "semantic-ui-react";
import { logDB, SignUp } from "api/db-actions";
import WithMessage from "./container/withMessage";
import Statistics from "components/container/statistics";
import { useHistory } from "react-router";
import { reducer } from "components/hooks/loginReducer";

export const Login: React.FC = () => {
  const initialState = {
    email: "",
    password: "",
    active: false,
    error: "",
    logged: false,
  };
  const [state, dispatch] = useReducer(reducer, initialState);
  const history = useHistory();

  const handleLogin = async () => {
    dispatch({ type: "active", payload: true });
    await logDB(state.email, state.password).then((res) =>
      res.success
        ? history.push("/")
        : dispatch({ type: "error", payload: res.error.message })
    );
  };

  const handleSignup = async () => {
    dispatch({ type: "active", payload: true });
    await SignUp(state.email, state.password).then((res) =>
      res.success
        ? history.push("/")
        : dispatch({ type: "error", payload: res.error.message.toUpperCase() })
    );
  };
  return (
    <React.Fragment>
      <div className="logo">
        <img
          alt="armut-logo"
          title="armut-logo"
          src="https://cdn.armut.com/images/tr:h-70/armut-header-logo-colour@2x.png"
        />
      </div>
      <WithMessage
        message={
          !state.error
            ? "Log in or Sign up so that we can save your important users"
            : state.error
        }
        header={
          !state.error ? "Welcome to Armut Test Tool Kit" : "Upps! Error..."
        }
      />

      <Segment placeholder>
        <Dimmer active={state.active}>
          <Loader indeterminate>Letting You In</Loader>
        </Dimmer>
        <Grid columns={2} relaxed="very" stackable>
          <Grid.Column>
            <Form>
              <Form.Input
                icon="user"
                iconPosition="left"
                label="Username"
                placeholder="Username"
                onChange={(e, data) =>
                  dispatch({ type: "email", payload: data.value })
                }
              />
              <Form.Input
                icon="lock"
                iconPosition="left"
                label="Password"
                type="password"
                placeholder="Password"
                onChange={(e, data) =>
                  dispatch({ type: "password", payload: data.value })
                }
              />
            </Form>
            <Button
              content="Login"
              icon="sign in"
              primary
              style={{ marginTop: "10px" }}
              onClick={handleLogin}
            />
          </Grid.Column>

          <Grid.Column verticalAlign="middle">
            <Form>
              <Form.Input
                icon="user"
                iconPosition="left"
                label="Username"
                placeholder="Username"
                onChange={(e, data) =>
                  dispatch({ type: "email", payload: data.value })
                }
              />
              <Form.Input
                icon="lock"
                iconPosition="left"
                label="Password"
                type="password"
                placeholder="Password"
                onChange={(e, data) =>
                  dispatch({ type: "password", payload: data.value })
                }
              />
            </Form>

            <Button
              content="Sign up"
              icon="signup"
              type="submit"
              color="vk"
              style={{ marginTop: "10px" }}
              onClick={handleSignup}
            />
          </Grid.Column>
        </Grid>

        <Divider vertical>Or</Divider>
      </Segment>
      <Statistics />
    </React.Fragment>
  );
};

export default Login;
