import { CardElement } from "../../assets/propTypes";
import { USER, DEFAULT_USER } from '../actionTypes';

const initialState: CardElement = {
    type: "Default",
    environment: "",
    job: false,
    country: 'tr',
    service: 156,
    quoteNumber: 2,
    deal: false,
    loading: false
};

type action = {
    type: string,
    payload: any
}


export default function (state = initialState, action: action) {
    switch (action.type) {

        case USER.TYPE: {
            return { ...state, type: action.payload }
        }
        case USER.ENVIRONMENT: {
            return { ...state, environment: action.payload }
        }
        case USER.COUNTRY: {
            return { ...state, country: action.payload }
        }
        case USER.JOB: {
            return { ...state, service: action.payload, job: true }
        }
        case USER.DEAL: {
            return { ...state, deal: action.payload }
        }
        case DEFAULT_USER: {
            return { ...state, }
        }
        default:
            return state
    }
}
