import React, { useState } from "react";
import { Grid, Dropdown } from "semantic-ui-react";
import { dispatchJob, dispatchCountry } from "store/actions";
import { connect, useSelector } from "react-redux";
import { getUserElements } from "store/selectors";

type PropTypes = { name: string; options: any; dispatchSelectable: any };

const WithSelect = (props: PropTypes) => {
  const selectType = props.name === "Job" ? "JOB" : "COUNTRY";
  const [select, handleSelect] = useState();
  const user = useSelector(getUserElements);
  const handleChange = (value: any) => {
    handleSelect(value);
    dispatchSelectable(selectType, value);
    console.log(user);
  };
  return (
    <React.Fragment>
      <Grid.Column textAlign="center" className="selectable">
        <Dropdown
          selection
          clearable
          placeholder={`Select ${props.name}`}
          options={props.options}
          value={select}
          onChange={(e, data) => handleChange(data.text)}
        />
      </Grid.Column>
    </React.Fragment>
  );
};

export default connect(null, { dispatchSelectable })(WithSelect);
