import React, { Fragment } from "react";
import WithSelect from "components/container/withSelect";
import { environmentOptions, userTypeOptions } from "assets/selectableOptions";
import { Message } from "semantic-ui-react";
import { useSelector } from "react-redux";
import { getUserElements } from "store/selectors";

const Header: React.FC = () => {
  const user = useSelector(getUserElements);
  return (
    <Fragment>
      {user.type === "Default" ? (
        <div style={{ margin: "0 25%" }}>
          <Message
            icon="info"
            header="Use sidebar hiding at the most left of the page "
            content="All users created are saved, dont worry"
            size="small"
            compact
          />
        </div>
      ) : null}
      <WithSelect
        name="Type"
        options={userTypeOptions}
        defaultValue="Default"
      />
      <WithSelect
        name="Environment"
        options={environmentOptions}
        defaultValue="Test"
      />
    </Fragment>
  );
};

export default Header;
