import React from "react";
import { Button, Card } from "semantic-ui-react";
import { CardElement } from "assets/propTypes";
import ClipBoardButton from "./withClipboard";
import { deleteUser } from "api/db-actions";

const WithCard: React.FC<{ card: CardElement; key: number; onDelete: any }> = ({
  card,
  key,
  onDelete,
}) => {
  const handleDelete = () => {
    deleteUser(card.id).then(() => onDelete(key));
  };
  return (
    <React.Fragment>
      <Card>
        <Card.Content>
          <Card.Header>
            <ClipBoardButton name={card.email} />
          </Card.Header>
          <Card.Meta>{card.id} here </Card.Meta>
          <Card.Description>
            <strong>{card.password} </strong>
            {card.accessToken}
          </Card.Description>
        </Card.Content>
        <Card.Content extra>
          <Button basic color="red" onClick={() => onDelete(key, card.id)}>
            Delete User
          </Button>
        </Card.Content>
      </Card>
    </React.Fragment>
  );
};

export default WithCard;
