import React from "react";
import { Card, Image, Flag, Label } from "semantic-ui-react";
import { CardElement } from "assets/propTypes";

const images = require.context("images", true);

const CustomUser = (props: CardElement) => (
  <Card centered>
    <Image
      circular
      src={images("./" + props.country + ".png")}
      wrapped
      ui={false}
      label={
        props.environment !== ""
          ? [
              {
                as: "a",
                color: props.environment === "Test" ? "red" : "blue",
                content: props.environment === "Test" ? "TEST" : "STAGE",
                ribbon: true,
              },
              { as: "a", corner: "right", icon: "heart" },
            ]
          : null
      }
    />
    <Card.Content>
      <Card.Header>
        <span>Serbest Meslek ErbabÄ±</span>
      </Card.Header>
      <Card.Meta>
        ShapeShifter
        <span>
          <Flag
            style={{ marginLeft: "4px" }}
            name={props.country}
            size="huge"
          />
        </span>
      </Card.Meta>
      <Card.Description>
        <div> Valar Morghulis</div>
      </Card.Description>
    </Card.Content>
    <Card.Content extra>
      something
      {/*       {["email", "password", "userId", "accessToken"].forEach((el) => {
        if (card.userId) {
          return ClipButtonHOC({ name: el, value: card.email });
        }
      })} */}
    </Card.Content>
  </Card>
);

export default CustomUser;
