import React from "react";
import WithCard from "components/container/withCard";
import { fetchUsers } from "api/db-actions";
import { Button, Dimmer, Loader, Grid } from "semantic-ui-react";
import { history } from "App";

type Props = {
  env: string;
};

export default class Saved extends React.Component<Props> {
  state = { users: [], loading: true };
  async componentDidMount() {
    const users = await fetchUsers(this.props.env);
    this.setState({ users, loading: false });
  }
  render() {
    const handleDelete = (id: number, user_id: string) => {
      this.setState({ ...this.state, loading: true });
    };
    return (
      <div>
        {this.state.loading ? (
          <Dimmer active={this.state.loading}>
            <Loader />
          </Dimmer>
        ) : this.state.users.length ? (
          <Grid columns="4">
            {this.state.users.map((user, id) => (
              <Grid.Column key={id}>
                <WithCard
                  card={user}
                  key={id}
                  onDelete={(id: number, user_id: string) =>
                    handleDelete(id, user_id)
                  }
                />
              </Grid.Column>
            ))}
          </Grid>
        ) : (
          <React.Fragment>
            <h1>There is no saved user</h1>
            <Button color="green" onClick={() => history.push("/")}>
              Go to Create One
            </Button>
          </React.Fragment>
        )}
      </div>
    );
  }
}
