import React, { useState } from "react";
import { Grid, Button } from "semantic-ui-react";
import { connect } from "react-redux";
import { Dispatch } from "redux";

type PropTypes = { left: string; right: string };

const WithButtonGroup: React.FC<PropTypes> = ({ left, right }) => {
  let [deal, setDeal] = useState<number>(0);

  const handleClick = (): void => {
    if (deal === 0) setDeal(1);
    else setDeal(0);
  };
  return (
    <React.Fragment>
      <Grid.Column textAlign="center" className="selectable">
        <Button.Group size="medium" style={{ marginTop: "30px" }}>
          with
          <Button type="button" positive={deal === 0} onClick={handleClick}>
            {left}
          </Button>
          />
          <Button.Or />
          <Button type="button" positive={deal === 1} onClick={handleClick}>
            {right}
          </Button>
        </Button.Group>
      </Grid.Column>
    </React.Fragment>
  );
};

const mapDispatchToProps = (dispatch: Dispatch) => {
  return {
    // dispatching plain actions
    deal: () => dispatch({ type: "DEAL" }),
  };
};

export default connect(null, mapDispatchToProps)(WithButtonGroup);
//export default WithButtonGroup;
