import { UserElement } from "../../assets/propTypes";
import { USER } from '../actionTypes';

const initialState: UserElement = {
  job: false,
  country: 'tr',
  service: 156,
  quoteNumber: 2,
  deal: false
};

type action = {
  type: string,
  payload: any
}


export default function (state = initialState, action: action) {
  switch (action.type) {
    case USER.ENVIRONMENT:

      {
        const response = { ...state }
        response.country = action.payload
        return response
      }
    case USER.COUNTRY: {
      const response = { ...state }
      response.country = action.payload
      return response
    }
    case USER.JOB: {
      const response = { ...state }
      response.service = action.payload
      response.job = true;
      return response
    }
    case USER.DEAL: {
      const response = { ...state }
      response.deal = action.payload
      return response
    }
    default:
      return state
  }
}
