import React from "react";
import { Button, Icon, Dimmer, Loader } from "semantic-ui-react";
import { CreateDefaultUser } from "../hooks/createDefaultUser";
import { useSelector } from "react-redux";
import { getUserElements } from "store/selectors";
import { client } from "api/db-actions";

const Submit: React.FC = () => {
  const user = useSelector(getUserElements);
  const [isLoading, loadUser] = CreateDefaultUser(user.environment);
  return (
    <React.Fragment>
      <Dimmer active={isLoading}>
        <Loader />
      </Dimmer>
      <Button
        icon
        labelPosition="left"
        id="saveUser"
        size="medium"
        color="instagram"
        type="button"
        style={{
          display: "inline-block",
          margin: "10px auto 10px 5px",
        }}
        onClick={() => loadUser(client.auth.user?.id)}
      >
        <Icon name="plus" />
        Create User
      </Button>
    </React.Fragment>
  );
};

export default Submit;
