import React, { useState } from "react";
import UserForm from "./components/Landing";
import "App.scss";
import { Button, Sidebar, Segment, Menu, Icon } from "semantic-ui-react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
function App() {
  const [visible, setVisibility] = useState(false);
  return (
    <div className="App">
      <Router>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            animation="overlay"
            icon="labeled"
            inverted
            onHide={() => setVisibility(false)}
            vertical
            visible={visible}
            width="thin"
          >
            <Menu.Item as="a">
              <Icon name="home" />
              <Link to="/berk">Home</Link>
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="play" />
              Test - Saved
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="camera" />
              Stage - Saved
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="log out" />
              Log Out
            </Menu.Item>
          </Sidebar>

          <Sidebar.Pusher>
            <Segment basic>
              <UserForm />
              <Button onClick={() => setVisibility(!visible)}>
                Show Sidebar
              </Button>
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
        <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/users">
            <Users />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

function Home() {
  return <h2>Home</h2>;
}

function About() {
  return <h2>About</h2>;
}

function Users() {
  return <h2>Users</h2>;
}

export default App;
