import { useState } from "react";
import store from "store/index";
import { dispatchDefaultUser } from "store/actions";
import { db } from "api/db-actions";

const payload = {
  operation: "CREATEUSER",
  commands: ["WITHPHONECONFIRMATION", "WITHPASSWORD"],
};

export function CreateDefaultUser(environment: string) {
  process.env["NODE_TLS_REJECT_UNAUTHORIZED"] = "0"; // to prevent SSL
  const url = `https://ttk.armut.${environment}/operate`;

  const [isLoading, setState] = useState(false);

  const loadUser = (id: string) => {
    setState(true);
    fetch(url, {
      method: "POST",
      body: JSON.stringify(payload),
    })
      .then((res) => res.json())
      .then((user) => {
        const { responseContext, error } = user;
        if (error) return;
        const { accessToken, password, model } = responseContext[0];
        const { user_id, email } = model;
        if (!error) {
          store.dispatch(
            dispatchDefaultUser({ user_id, email, password, accessToken })
          );
          db.collection("test").insertOne({
            user_id: id,
            id: user_id,
            environment,
            email,
            password,
            accessToken,
          });
        }
      })
      .catch(() => {
        store.dispatch(
          dispatchDefaultUser({
            user_id: "123",
            email: "12312",
            password: "12313",
            accessToken: "accessToken",
          })
        );
      })
      .finally(() => {
        setState(false);
      });
  };
  return [isLoading, loadUser] as const; // infers [boolean, typeof load] instead of (boolean | typeof load)[]
}
