import React, { useState, useEffect } from "react";
import { Grid, Button } from "semantic-ui-react";
import { connect } from "react-redux";

const WithButtonGroup = ({
  left,
  right,
  dispatch,
}: {
  left: string;
  right: string;
  dispatch: any;
}) => {
  let [deal, setDeal] = useState(0);
  useEffect(() => {
    dispatch({ type: "DEAL" });
  }, [deal, dispatch]);
  return (
    <React.Fragment>
      <Grid.Column textAlign="center" className="selectable">
        <Button.Group size="medium" style={{ marginTop: "30px" }}>
          with
          <Button
            type="button"
            positive={deal === 0}
            onClick={() => setDeal(0)}
          >
            {left}
          </Button>
          />
          <Button.Or />
          <Button
            type="button"
            positive={deal === 1}
            onClick={() => setDeal(1)}
          >
            {right}
          </Button>
        </Button.Group>
      </Grid.Column>
    </React.Fragment>
  );
};

const mapDispatchToProps = (dispatch: any) => {
  return {
    // dispatching plain actions
    deal: () => dispatch({ type: "DEAL" }),
  };
};

export default connect(null, mapDispatchToProps, WithButtonGroup);
